import React from 'react';
import '../styles/Projects.css';

const projects = [
  { title: 'Acoustic Rootworm Repellent (ESP32)', desc: 'ESP32 based ultrasonic acoustic repellent' },
  { title: 'Biomedical Waste Segregation – Delta Robotic Arm', desc: 'Delta arm for sorting biomedical waste' },
  { title: 'Self-Balancing Robot (PID + LQR)', desc: 'Control systems and sensor fusion for balancing' },
  { title: 'LED Matrix Display (STM32F411RE)', desc: 'Custom driver and animations on an LED matrix' }
];

export default function Projects() {
  return (
    <section className="projects reveal container">
      <h2 className="page-title">Projects</h2>
      <div className="project-grid">
        {projects.map((p, i) => (
          <article className="project-card" key={i}>
            <div className="project-inner">
              <h3>{p.title}</h3>
              <p>{p.desc}</p>
              <div className="project-links">
                <a href="#" className="btn small neon">View</a>
              </div>
            </div>
          </article>
        ))}
      </div>
    </section>
  );
}
