import React, { useEffect, useState } from 'react';
import '../styles/Home.css';

const typingList = [
  'Embedded Systems',
  'Robotics',
  'IoT Systems',
  'PID & Motion Control'
];

function Typing() {
  const [i, setI] = useState(0);
  const [pos, setPos] = useState(0);
  const [forward, setForward] = useState(true);

  useEffect(() => {
    const t = setInterval(() => {
      if (forward) {
        if (pos < typingList[i].length) setPos((p) => p + 1);
        else setForward(false);
      } else {
        if (pos > 0) setPos((p) => p - 1);
        else {
          setForward(true);
          setI((x) => (x + 1) % typingList.length);
        }
      }
    }, 120);
    return () => clearInterval(t);
  }, [i, pos, forward]);

  return <span className="typing">{typingList[i].slice(0, pos)}<span className="cursor">|</span></span>;
}

export default function Home() {
  return (
    <section className="home reveal container">
      <div className="home-inner">
        <div className="left">
          <div className="profile-wrap floating profile-glow">
            <img src="/assets/profile.svg" alt="Gowtham" className="profile" />
          </div>
        </div>

        <div className="right">
          <h1 className="name glow-text">GOWTHAM C</h1>
          <h2 className="title">Electronics &amp; Communication Engineering | Embedded &amp; Robotics Developer</h2>

          <p className="lead">
            Electronics and Communication Engineering student skilled in embedded systems, robotics, and IoT development. Experienced
            in real-time microcontrollers and sensor-based automation. Passionate about building hardwareâ€“software systems that solve
            real-world problems.
          </p>

          <div className="typing-row">
            <Typing />
          </div>

          <div className="cta-row">
            <a className="btn neon" href="/assets/Gowtham_Resume.pdf" role="button">Download Resume</a>
            <a className="btn ghost" href="/projects">View My Work</a>
          </div>

          <div className="feature-strip">
            <div className="feature-card zoom-in">Technical Strengths<br/><span className="muted">Embedded, Control, Sensors</span></div>
            <div className="feature-card zoom-in">Achievements<br/><span className="muted">e-Yantra, IIT Bombay</span></div>
            <div className="feature-card zoom-in">Patent<br/><span className="muted">Smart acoustic rootworm repellent</span></div>
            <div className="feature-card zoom-in">Robotics Projects<br/><span className="muted">Delta Arm, Self-balancing</span></div>
          </div>
        </div>
      </div>
    </section>
  );
}
