import React from 'react';
import '../styles/Experience.css';

const experiences = [
  {
    title: 'e-Yantra Robotics Intern – IIT Bombay',
    points: ['Self-balancing robot using PID', 'Obstacle avoidance', 'Sensor fusion + control systems']
  },
  {
    title: 'Pumo Technovation – Embedded Systems',
    points: ['8051, PIC, ARM Cortex, STM32', 'Keil, MPLAB, STM32CubeIDE', 'Real-time systems & hardware interfacing']
  }
];

export default function Experience() {
  return (
    <section className="experience reveal container">
      <h2 className="page-title">Experience</h2>
      <div className="cards">
        {experiences.map((e, idx) => (
          <div className="exp-card" key={idx}>
            <h3>{e.title}</h3>
            <ul>
              {e.points.map((p, i) => (
                <li key={i}>{p}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
}
